import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import quizData from '../quizData';
import Confetti from './Confetti';
import './Quiz.css';

function QuizGame({ playerName }) {
  const [backgroundColor, setBackgroundColor] = useState('default');
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [timeLeft, setTimeLeft] = useState(10);
  const [showConfetti, setShowConfetti] = useState(false);
  const [reachedQuizEnd, setReachedQuizEnd] = useState(false);
  const [capturedImages, setCapturedImages] = useState([]);
  const [uploadedImageUrls, setUploadedImageUrls] = useState([]);

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const captureIntervalRef = useRef(null);
  const timerRef = useRef(null);

  // Start webcam and begin face capture
  const startWebcamCapture = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { width: 640, height: 480 },
      });

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.onloadedmetadata = () => {
          videoRef.current.play();
        };

        captureIntervalRef.current = setInterval(() => {
          captureImage();
        }, 5000);
      }
    } catch (error) {
      console.error('Error accessing webcam:', error);
    }
  };

  // Stop webcam capture
  const stopWebcamCapture = () => {
    if (captureIntervalRef.current) {
      clearInterval(captureIntervalRef.current);
      captureIntervalRef.current = null;
    }

    if (videoRef.current && videoRef.current.srcObject) {
      const tracks = videoRef.current.srcObject.getTracks();
      tracks.forEach((track) => track.stop());
      videoRef.current.srcObject = null;
    }
  };

  const captureImage = async () => {
    if (canvasRef.current && videoRef.current) {
      const canvas = canvasRef.current;
      const context = canvas.getContext('2d');
      context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

      const imageDataURL = canvas.toDataURL('image/jpeg');
      const base64Response = await fetch(imageDataURL);
      const blob = await base64Response.blob();

      await handleImageUpload(blob);

      setCapturedImages((prevImages) => [...prevImages, imageDataURL]);
    }
  };

  // Handle image upload with username
  const handleImageUpload = async (imageBlob) => {
    try {
      const formData = new FormData();
      formData.append('image', imageBlob, 'captured-image.jpg');
      formData.append('username', playerName); // Include the username in the request

      const response = await axios.post('http://localhost:5000/api/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      if (response.data.fileUrl) {
        setUploadedImageUrls((prev) => [...prev, response.data.fileUrl]);
      }
    } catch (error) {
      console.error('Error uploading image:', error);
    }
  };

  const startTimer = () => {
    if (timerRef.current) clearInterval(timerRef.current); // Clear any previous timers
    setTimeLeft(10);

    timerRef.current = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev === 1) {
          clearInterval(timerRef.current);
          handleNextQuestion();
        }
        return prev - 1;
      });
    }, 1000);
  };

  const handleNextQuestion = () => {
    setBackgroundColor('default');
    if (currentQuestion < quizData.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setTimeLeft(30);
    } else {
      setReachedQuizEnd(true);
      setShowScore(true);
      setShowConfetti(true);
      stopWebcamCapture();
      clearInterval(timerRef.current);
    }
  };

  useEffect(() => {
    startWebcamCapture();
    startTimer();

    return () => {
      stopWebcamCapture();
      clearInterval(timerRef.current);
    };
  }, []);

  const handleAnswerOptionClick = (index) => {
    if (index === quizData[currentQuestion].answer) {
      setScore(score + 1);
      setBackgroundColor('correct');
    } else {
      setBackgroundColor('incorrect');
    }
    setTimeout(handleNextQuestion, 500);
  };

  const handleRestartQuiz = () => {
    setCurrentQuestion(0);
    setScore(0);
    setShowScore(false);
    setTimeLeft(30);
    setShowConfetti(false);
    setBackgroundColor('default');
    setReachedQuizEnd(false);
    setCapturedImages([]);
    setUploadedImageUrls([]);
    startWebcamCapture();
    startTimer();
  };

  return (
    <div className={`one ${backgroundColor}`}>
      <div className="quiz">
        {/* Video and Canvas for Webcam */}
        <div className="webcam-container">
          <video ref={videoRef} style={{ display: 'none' }}></video>
          <canvas
            ref={canvasRef}
            width={640}
            height={480}
            style={{ display: 'none' }}
          ></canvas>
        </div>

        {showConfetti && <Confetti />}
        {showScore ? (
          <div className="score-section">
            <h1>ðŸŽ‰ Congratulations {playerName}! ðŸŽ‰</h1>
            <h2>Your Score: {score} / {quizData.length}</h2>
            <button onClick={handleRestartQuiz}>Restart Quiz</button>
          </div>
        ) : (
          <>
            <div className="progress-bar">
              <div
                className="progress-bar-fill"
                style={{
                  width: `${((currentQuestion + 1) / quizData.length) * 100}%`,
                }}
              ></div>
            </div>
            <div className="timer">Time Left: {timeLeft}s</div>
            <div className="question-section">
              <h2>{quizData[currentQuestion].question}</h2>
              {quizData[currentQuestion].image && (
                <img
                  src={quizData[currentQuestion].image}
                  alt="quiz illustration"
                />
              )}
              <div className="options">
                {quizData[currentQuestion].options.map((option, index) => (
                  <button
                    key={index}
                    onClick={() => handleAnswerOptionClick(index)}
                    className="option-button"
                  >
                    {option}
                  </button>
                ))}
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default QuizGame;
